import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useFormik } from 'formik';
import { useAuthStore } from '../store/store';
import Navbar from '../components/Nav';
import toast, { Toaster } from "react-hot-toast";
import { BsSearch } from "react-icons/ai";

export default function Admin() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [filterValue, setFilterValue] = useState('');
  const [items, setItems] = useState([]);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
    // Perform search logic based on the searchTerm
    // Update the items state with the filtered items
  };

  const handleFilter = (event) => {
    setFilterValue(event.target.value);
    // Perform filtering logic based on the filterValue
    // Update the items state with the filtered items
  };

  const handleAddItem = () => {
    // Logic for adding an item
    // Redirect or show a modal to add an item
  };

  return (
    <main>
        <Navbar />
        <div className="container mx-auto">
            <Toaster position="top-center" reverseOrder={false}></Toaster>
            <h1 className='px-8 py-2 text-3xl font-ebold tracking-wider'>BEARBRICK Database</h1>
            <div className="flex flex-row justify-center items-center">
                <input
                type="text"
                placeholder="  Search"
                value={searchTerm}
                onChange={handleSearch}
                className="rounded-xl p-2"
                />

                <button
                onClick={handleAddItem}
                className="bg-white text-black px-4 py-2 rounded-xl ml-2 hover:bg-black hover:text-white"
                >Add Item</button>

<button
                onClick={handleFilter}
                className="bg-white text-black px-4 py-2 rounded-xl ml-2"
                >Filter</button>
                
                {/* Render the items */}
                <ul>
                    {items.map((item) => (
                    <li key={item.id}>{item.name}</li>
                    ))}
                </ul>
            </div>
        </div>
        
        
    </main>
  );
}
