import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useFormik } from 'formik';
import { useAuthStore } from '../store/store';
import Navbar from '../components/Nav';
import toast, { Toaster } from "react-hot-toast";
import { BsSearch } from "react-icons/bs";
import { FaSearch, FaTimes } from 'react-icons/fa';

export default function Admin() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [filterValue, setFilterValue] = useState('');
  const [items, setItems] = useState([]);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleClearSearch = () => {
    setSearchTerm('');
    // Clear the search term and update the items state accordingly
  };

  const handleFilter = (event) => {
    setFilterValue(event.target.value);
  };

  const handleAddItem = () => {
  };

  return (
    <main>
        <Navbar />
        <div className="container mx-auto">
            <Toaster position="top-center" reverseOrder={false}></Toaster>
            <h1 className='px-8 py-2 text-3xl font-bold tracking-wider'>BEARBRICK Database</h1>
            <div className="flex flex-row justify-center items-center">
                <input
                type="text"
                placeholder="  Search"
                value={searchTerm}
                onChange={handleSearch}
                className="w-4/5 h-12 border border-gray-400 rounded-xl p-2 focus:outline-none focus:ring-2 focus:ring-gray-400 focus:border-transparent"
                />

                <button
                onClick={handleAddItem}
                className="bg-white text-black px-4 py-2 rounded-xl ml-2 hover:bg-black hover:text-white"
                >Add Item</button>

<button
                onClick={handleFilter}
                className="bg-white text-black px-4 py-2 rounded-xl ml-2"
                >Filter</button>
                
                {/* Render the items */}
                <ul>
                    {items.map((item) => (
                    <li key={item.id}>{item.name}</li>
                    ))}
                </ul>
            </div>
        </div>
        
        
    </main>
  );
}
