import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useFormik } from 'formik';
import { useAuthStore } from '../store/store';
import Navbar from '../components/Nav';
import toast, { Toaster } from "react-hot-toast";
import { BsSearch } from "react-icons/bs";
import { FaSearch, FaTimes } from 'react-icons/fa';

export default function Admin() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [filterValue, setFilterValue] = useState('');
  const [items, setItems] = useState([]);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleClearSearch = () => {
    setSearchTerm('');
  };

  const handleFilter = (event) => {
    setFilterValue(event.target.value);
  };

  const handleAddItem = () => {
  };
    // <h1 className='px-8 py-2 text-3xl font-bold tracking-wider'>BEARBRICK Database</h1>
  return (
    <main>
      <Navbar />
      <h1 className='flex mx-auto justify-start items-center px-32 py-2 text-3xl font-bold tracking-wider'
      >BEARBRICK Database</h1>
      <hr class="h-px my-8 bg-gray-200 border-0 dark:bg-gray-00"></hr>
      <div className="flex items-center">
        
        <div className="relative">
          <input
            type="text"
            placeholder="Search"
            value={searchTerm}
            onChange={handleSearch}
            className="w-4/5 h-12 border border-gray-400 rounded-xl p-2 pl-10 pr-12 focus:outline-none focus:ring-2 focus:ring-gray-400 focus:border-transparent"
          />
          <FaSearch className="absolute top-3 left-3 text-gray-500" />
          {searchTerm && (
            <button
              className="absolute top-3 right-3 text-gray-500"
              onClick={handleClearSearch}
            >
              <FaTimes />
            </button>
          )}
        </div>
        <button
          onClick={handleFilter}
          className="bg-blue-500 text-white px-4 py-2 rounded-md ml-2"
        >
          Filter
        </button>
        <button
          onClick={handleAddItem}
          className="bg-green-500 text-white px-4 py-2 rounded-md ml-2"
        >
          Add Item
        </button>
      </div>
      {/* Render the items */}
      <ul>
        {items.map((item) => (
          <li key={item.id}>{item.name}</li>
        ))}
      </ul>
    </main>
  );
}
