import React from 'react'
import { Link, useNavigate } from 'react-router-dom'
import avatar from '../assets/profile.jpg';
import toast, { Toaster } from 'react-hot-toast';
import { useFormik } from 'formik';
import { passwordValidate } from '../helper/validate'
import useFetch from '../hooks/fetch.hook';
import { useAuthStore } from '../store/store'
import { verifyPassword } from '../helper/helper'
import styles from '../styles/Landing.module.css';

export default function LandingPage() {

//   const [file, setFile] = useState();
//   const [{ isLoading, apiData, serverError }] = useFetch();
//   const navigate = useNavigate()
  
//   const formik = useFormik({
//     initialValues : {
//       username: apiData?.username || '',
//       email: apiData?.email || '',
//       bio: apiData?.bio || ''
//     },
//     enableReinitialize: true,
//     validate : profileValidation,
//     validateOnBlur: false,
//     validateOnChange: false
//   })


//   if(isLoading) return <h1 className='text-2xl font-bold'>isLoading</h1>;
//   if(serverError) return <h1 className='text-xl text-red-500'>{serverError.message}</h1>

    return (
        <main>
          <nav className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex items-center justify-between h-28">
              <div className="flex items-center">
                <h1 className="text-neutral-800 text-2xl font-semibold">B E @ R</h1>
              </div>
              <div className="flex justify-center flex-grow">
                <div className="flex space-x-4">
                  <a
                    href="#"
                    className="text-neutral-900 hover:bg-neutral-900 hover:text-white px-3 py-2 rounded-md text-lg font-medium"
                  >
                    Social
                  </a>
                  <a
                    href="#"
                    className="text-neutral-900 hover:bg-neutral-900 hover:text-white px-3 py-2 rounded-md text-lg font-medium"
                  >
                    Collection
                  </a>
                  <a
                    href="#"
                    className="text-neutral-900 hover:bg-neutral-900 hover:text-white px-3 py-2 rounded-md text-lg font-medium"
                  >
                    QR Code
                  </a>
                </div>
              </div>
              <div className="flex items-center">
                <div className="h-16 w-16 rounded-full bg-gray-900">
                    
                </div>
              </div>
            </div>
          </nav>

            <div className="container mx-auto">
                <Toaster position='top-center' reverseOrder={false}></Toaster>
                <div className='flex flex-col justify-center items-center h-screen'>
                    <div className={styles.glass}>
                        <div className="title flex flex-col items-left py-24">
                            <h4 className="text-gray-800 text-4xl px-16 font-base py-2">Welcome to</h4>
                            <h4 className="text-gray-800 text-6xl px-16 font-bold py-4">BE@RBRICK world</h4>
                            <span className='text-gray-350 text-lg w-2/3 text-left px-16 py-2'>
                                Share the world with your cool bearbrick!
                            </span>
                            <div className="w-5 h-5 rounded-full bor"></div>
                            <span className='text-gray-350 text-lg w-2/3 text-left px-16 py-8'>
                                Let's create you bearbrick collection
                            </span>
                            <button className={styles.btn} type='button'>Create</button>
                            
                        </div>
                    </div>
                </div>
            </div>
        </main>
    )
}
